mapper.select:
SELECT ID,ADDRESS,ADJUSTED_LAT,ADJUSTED_LNG,CREATED_LOCATION_TIME,LAT,LNG,CEIL(SQRT(POWER(((SELECT ADJUSTED_LAT FROM LOCATION WHERE id = #{id}) - ADJUSTED_LAT) * 91, 2) + POWER(((SELECT ADJUSTED_LNG FROM LOCATION WHERE id = #{id}) - ADJUSTED_LNG) * 113, 2))) as distance FROM location
WHERE id != #{id}
ORDER BY SQRT(POWER(((SELECT ADJUSTED_LAT FROM LOCATION WHERE id = #{id}) - ADJUSTED_LAT) * 91000, 2) + POWER(((SELECT ADJUSTED_LNG FROM LOCATION WHERE id = #{id}) - ADJUSTED_LNG) * 113000, 2)) asc
